var barbiche = Barbiche();
barbiche('basic').merge({
	class: "barbiche",
	attribute: {name: "my-attr", value: "my-val"},
	text: "Hello World!"
});

var barbiche = Barbiche();
barbiche('starter').merge({
	customClass: "my-class",
	"items": [
		{species: "hen", name: "Elsa", show: true},
		{species: "cat", name: "Jacynthe", show: false},
		{species: null, name: "Zaza", show: true}
	],
	item: ["hen", "cat", "dog", "spider"],
	some_html: "<div><p>This is...</p><p>...some HTML.</p></div>",
	my_replace: function(str) {return str.replace(/o/g, "O");},
	attrValue: "lunchroom"
});

var barbiche = Barbiche();
barbiche('conditions').merge({
	"items": [
		{name: "Elsa", show: true},
		{name: "Jacynthe", show: false}
	]
});

var barbiche = Barbiche();
var obj = {
	outer: {
		inner: {
			name: "Zaza"
		}
	}
};
barbiche('aliases').merge(obj);

var barbiche = Barbiche();
var obj = {
	yes: "You see me",
	again: "You see me too",
	no: "You won't see me",
	maybe: "Well, maybe...",
	some_html: "<div><p>This is...</p><p>...some HTML.</p></div>"
};
barbiche('text').merge(obj);
barbiche('html').merge(obj);

var barbiche = Barbiche();
var data = {
	items: [
		{species: "hen", name: "Elsa", show: true},
		{species: "cat", name: "Jacynthe", show: false},
		{species: null, name: "Zaza", show: true}
	]
};
barbiche('loop').merge(data);
barbiche('descending-loop').merge(data);
barbiche('table').merge({
	rows: [
		["A1", "B1", "C1"],
		["A2", "B2", "C2"]
	]
});
barbiche('nested-loops').merge({
	arr1: ["A", "B"],
	arr2: [1, 2, 3]
});

var barbiche = Barbiche();
barbiche('simple-subtemplate').merge();
barbiche('dynamic-subtemplate').merge({
	items: [{name: 'item1', sub: 'sub2'}, {name: 'item2', sub: 'sub1'}]
});
barbiche('recursive-subtemplate').merge({
	children: [
		{
			name: "Solomon",
			children: [
				{
					name : "Lexie",
					children: [
						{
							name: "Shavon",
							children: [
								{
									name : "Shirleen"
								}, {
									name : "Kathleen"
								}
							]
						},
						{
							name: "Travis"
						},
						{
							name: "Adele"
						},
						{
							name: "Barney"
						}
					]
				}, {
					name : "Allie"
				}
			]
		}, {
			name: "Juliann",
			children: [
				{
					name : "Guy"
				}, {
					name : "Yaeko"
				}
			]
		}
	]
});

var barbiche_bracket = Barbiche({delimiters: ['[', ']']});
barbiche_bracket('custom-delimiters').merge({
	"text": "Hello World!"
});

var barbiche = Barbiche();
barbiche('escaped-delimiters').merge({
	"text": "Hello World!"
});

var barbiche = Barbiche();
barbiche('identifiers').merge({
	"text": "Hello earth!",
	"t-e{ }x-t": "Hello Mars!",
	"\u03A9 te\n\t \\`{{xt": "Hello Moon!"
});
