var barbiche = Barbiche();
target.appendChild(barbiche('basic').merge({
	class: "barbiche",
	attribute: {name: "my-attr", value: "my-val"},
	text: "Hello World!"
}));

var barbiche = Barbiche();
target.appendChild(barbiche('starter').merge({
	customClass: "my-class",
	"items": [
		{species: "hen", name: "Elsa", show: true},
		{species: "cat", name: "Jacynthe", show: false},
		{species: null, name: "Zaza", show: true}
	],
	item: ["hen", "cat", "dog", "spider"],
	some_html: "<div><p>This is...</p><p>...some HTML.</p></div>",
	my_replace: function(str) {return str.replace(/o/g, "O");},
	attrValue: "lunchroom"
}));

var barbiche = Barbiche();
target.appendChild(barbiche('conditions').merge({
	"items": [
		{name: "Elsa", show: true},
		{name: "Jacynthe", show: false}
	]
}));
target.appendChild(barbiche('nested-conditions').merge({
	bool1: false,
	bool2: false
}));

var barbiche = Barbiche();
var obj = {
	outer: {
		inner: {
			name: "Zaza"
		}
	}
};
target.appendChild(barbiche('aliases').merge(obj));

var barbiche = Barbiche();
var obj = {
	yes: "You see me",
	again: "You see me too",
	no: "You won't see me",
	maybe: "Well, maybe...",
	some_html: "<div><p>This is...</p><p>...some HTML.</p></div>",
	some_node: function() {
		var elt = document.createElement('p');
		elt.appendChild(document.createTextNode('Some really nice text'));
		return elt;
	}
};
target.appendChild(barbiche('text').merge(window, obj));
target.appendChild(barbiche('html').merge(obj));

var barbiche = Barbiche();
var data = {
	items: [
		{species: "hen", name: "Elsa", show: true},
		{species: "cat", name: "Jacynthe", show: false},
		{species: null, name: "Zaza", show: true}
	]
};
target.appendChild(barbiche('loop').merge(data));
target.appendChild(barbiche('reverse-loop').merge(data));
target.appendChild(barbiche('table').merge({
	rows: [
		["A1", "B1", "C1"],
		["A2", "B2", "C2"]
	]
}));
target.appendChild(barbiche('nested-loops').merge({
	arr1: ["A", "B"],
	arr2: [1, 2, 3]
}));

var barbiche = Barbiche();
target.appendChild(barbiche('simple-subtemplate').merge());
target.appendChild(barbiche('dynamic-subtemplate').merge({
	items: [{name: 'item1', sub: 'sub2'}, {name: 'item2', sub: 'sub1'}]
}));
target.appendChild(barbiche('data-subtemplate').merge({
	name: 'item',
	data_sub: '<span>I am a subtemplate! ({{name}})</span>'
}));
target.appendChild(barbiche('recursive-subtemplate').merge({
	children: [
		{
			name: "Solomon",
			children: [
				{
					name : "Lexie",
					children: [
						{
							name: "Shavon",
							children: [
								{
									name : "Shirleen"
								}, {
									name : "Kathleen"
								}
							]
						},
						{
							name: "Travis"
						},
						{
							name: "Adele"
						},
						{
							name: "Barney"
						}
					]
				}, {
					name : "Allie"
				}
			]
		}, {
			name: "Juliann",
			children: [
				{
					name : "Guy"
				}, {
					name : "Yaeko"
				}
			]
		}
	]
}));

var barbiche = Barbiche();
var bbObj = barbiche.bbObj;
target.appendChild(barbiche('attributes').merge({
	width: 'full-width',
	height: 'half',
	diag: null,
	name: 'depth',
	value: 5,
	attr: bbObj('value', 'name'),
	attrList: [bbObj('value1', 'name1'), bbObj('value2', 'name2')]
}));

var barbiche = Barbiche();
target.appendChild(barbiche('classes').merge({
	myClass: "barbiche",
	isCurrentUrl: function(url) {return url == "home";},
	page1: "home",
	page2: "doc",
	myClasses: ['cl1', 'cl2', 'cl3'],
}));

var barbiche = Barbiche();
var obj = {
	options1: {weekday: "long", year: "numeric", month: "long", day: "numeric", hour: "numeric", minute: "numeric"},
	options2: {weekday: "long", year: "numeric", month: "long", day: "numeric"},
	some_JSON: {"a": 1, "b": {"c": 5}}
};
var helpers = {
	now: function(opts) {return new Date();},
	JSON: window.JSON
};
target.appendChild(barbiche('context').merge(helpers, obj));
target.appendChild(barbiche('window').merge(window, obj));

var barbiche_bracket = Barbiche({delimiters: ['[', ']']});
target.appendChild(barbiche_bracket('custom-delimiters').merge({
	"text": "Hello World!"
}));

var barbiche = Barbiche();
target.appendChild(barbiche('escaped-delimiters').merge({
	"text": "Hello World!"
}));

var barbiche = Barbiche();
target.appendChild(barbiche('identifiers').merge({
	"text": "Hello Earth!",
	"t-e{ }x-t": "Hello Mars!",
	"\u03A9 te\n\t \\`{{xt": "Hello Moon!"
}));

var barbiche = Barbiche();
target.appendChild(barbiche('inert').merge({
	myClass: 'lazy',
	myId: 'art-49',
	text: 'Inert template element'
}));
